// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts v4.3.2 (utils/DefaultCrowdsalev2.sol)
pragma solidity ^0.8.9;
import "./CrowdsaleBase.sol";
import "./WhitelistedRole.sol";
import "../node_modules/@openzeppelin/contracts/security/Pausable.sol";
import "../node_modules/@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./StageAdminRole.sol";
/**
 * @title DefaultCrowdsale
 * @dev This is an example of a fully fledged crowdsale.
 */
contract DefaultCrowdsalev2 is CrowdsaleBase, StageAdminRole, Pausable {
    using SafeMath for uint256;

    struct StageExchangeRate {
        address token;
        uint256 rate;
    }

    struct StageSettings {
        string name;
        bool isPrivate;
        uint256 cap;
        uint256 minIndividualCap;
        uint256 maxIndividualCap;
        address[] whitelist;
        uint256 rate;
    }

    uint internal _nextStage;
    uint public numberOfStages;
    StageSettings[] stages;
    StageSettings _currentStage;

    mapping(uint => StageExchangeRate[]) exchangeRates;
    StageExchangeRate[] stageExchangeRates;

    uint256[] private _stageWeiRaised;

    mapping(uint => mapping(address => uint256)) _stageTokensRaised;
    mapping(address => uint256) private _tokensRaised;

    event NextStage(StageSettings stage);

    constructor(
        address payable wallet,
        IERC20 token,
        uint _numberOfStages
    ) CrowdsaleBase(1, wallet, token) {
        _nextStage = 0;
        numberOfStages = _numberOfStages;
    }

    /**
    * @dev Change funding to next rate value
    */
    function startNextStage(StageSettings memory settings, StageExchangeRate[] memory exchangeRates_) public onlyStageAdmins virtual {
        require(_nextStage <= numberOfStages, "Crowdsale: Stage exceeds stage limit");
        super._updateRate(settings.rate);
        stages.push(settings);
        _stageWeiRaised.push(0);
        _currentStage = settings;
        _nextStage += 1;
        _removeAllWhitelisted();
        addWhitelisted(settings.whitelist);

        delete stageExchangeRates;
        for (uint i = 0; i < exchangeRates_.length; i++) {
            stageExchangeRates.push(exchangeRates_[i]);
            exchangeRates[_nextStage - 1].push(exchangeRates_[i]);  
        }

        remainStageTokens = settings.cap;

        if(super.paused()){
            super._unpause();
        }
        emit NextStage(settings);
    }

    function pause() public onlyStageAdmins whenNotPaused virtual {
         super._pause();
    }

    function stage() public view returns (string memory) {
        return _currentStage.name;
    }

    function cap() public view returns (uint256) {
        return _currentStage.cap;
    }

    function _addContributor(address beneficiary) virtual internal override {
        if(_contributions[beneficiary].length == 0){
            _contributions[beneficiary] = new uint256[](numberOfStages);
        }
    }

    /**
     * @dev Extend parent behavior requiring purchase to respect the funding cap.
     * @param beneficiary Token purchaser
     * @param weiAmount Amount of wei contributed
     */
    function _preValidatePurchase(address beneficiary, uint256 weiAmount, uint256 tokenAmount) virtual internal view override whenNotPaused {
        super._preValidatePurchase(beneficiary, weiAmount, tokenAmount);
        require(_currentStage.rate >= 0 && _nextStage > 0, "Crowdsale: Stage is not active");
        require(_currentStage.isPrivate != true || isWhitelisted(beneficiary), "Ongoing private sale: benefeciary is not white listed");
        require(tokenAmount <= remainStageTokens, "CappedCrowdsale: cap exceeded");
        require(_contributions[beneficiary][_nextStage - 1].add(tokenAmount) >= _currentStage.minIndividualCap && _contributions[beneficiary][_nextStage - 1].add(tokenAmount) <= _currentStage.maxIndividualCap,  "Crowdsale: Individual cap limit was not met");
    }

    /**
     * @dev Extend parent behavior to update beneficiary contributions.
     * @param beneficiary Token purchaser
     * @param weiAmount Amount of wei contributed
     */
    function _updatePurchasingState(address beneficiary, uint256 weiAmount, uint256 tokenAmount) internal override {
        super._updatePurchasingState(beneficiary, weiAmount, tokenAmount);
        _contributions[beneficiary][_nextStage - 1] = _contributions[beneficiary][_nextStage - 1].add(tokenAmount);
        _stageWeiRaised[_nextStage - 1] = _stageWeiRaised[_nextStage - 1].add(weiAmount);
        remainStageTokens = remainStageTokens.sub(tokenAmount);
    }

    /**
     * This function allow exchange erc20 which has added rate is set
     * @param paymentToken Token we want exchange for crowdsale token
     * @param amount Amount of token to exchange
     */
    function exchangeTokens(address paymentToken, uint256 amount, address beneficiary) public nonReentrant {
        require(IERC20(paymentToken).allowance(_msgSender(), address(this)) >= amount, "Invalid token allowance");
         
        uint256 exchangeRate = findTokenRate(paymentToken);  
        uint256 crowdsaleTokensAmount = amount.div(exchangeRate);
        
        _addContributor(beneficiary);
       
        _preValidatePurchase(beneficiary, amount, crowdsaleTokensAmount);

        uint currentStageIndex = _nextStage - 1;
        
        // code copied mostly from _updatePurchasingState
        // weiRaised is ommitted because token are purchasing by other currency than ether
        _contributions[beneficiary][currentStageIndex] = _contributions[beneficiary][currentStageIndex].add(crowdsaleTokensAmount);
        _tokensRaised[paymentToken] = _tokensRaised[paymentToken].add(amount);
        _stageTokensRaised[currentStageIndex][paymentToken] = _stageTokensRaised[currentStageIndex][paymentToken].add(amount);
        remainStageTokens = remainStageTokens.sub(crowdsaleTokensAmount);

        emit TokensPurchased(beneficiary, beneficiary, amount, crowdsaleTokensAmount);

        IERC20(paymentToken).transferFrom(_msgSender(), _wallet, amount);
        IERC20(_token).transferFrom(_wallet, beneficiary, crowdsaleTokensAmount);
    }

    function findTokenRateIndex(address token) private view returns(uint) {
        for (uint i=0; i < stageExchangeRates.length; i++) {
            if (stageExchangeRates[i].token == token) {
                return i;
            }
        }
        revert("Token rate not found");
    }

    function findTokenRate(address token) public view returns(uint256) {
        uint tokenRateIndex = findTokenRateIndex(token);
        return stageExchangeRates[tokenRateIndex].rate;
    }

}

